	** Error Response | 404 invalid_property_uid / 403 invalid_property_uid
	** Sample call |jomres/api/webhooks/1/tariffs_updated
	** Notes | Replies with the details of the property tariffs. This is a straight dump from the database so will contain a very large amount of data for properties with many tariffs.
*/

Flight::route('GET /webhooks/@id/tariffs_updated', function($property_uid)
	{
    $property_uid = (int)$property_uid;
    
	validate_scope::validate('webhooks_get');
	validate_property_access::validate($property_uid);

	if (!isset($language))
		$language = "en-GB";
	$_REQUEST['jomreslang'] = $language;
	require_once("../framework.php");

    try {
        $allPropertyTariffs = array();
        $today = date('Y/m/d', mktime(0, 0, 0, date('m'), date('d'), date('Y')));
        $mrConfig	 = getPropertySpecificSettings( $property_uid );
        $accommodation_tax_rate = 0.0;
        if (isset($mrConfig[ 'accommodation_tax_code' ]) && (int) $mrConfig[ 'accommodation_tax_code' ] > 0) {
            $jrportal_taxrate = jomres_singleton_abstract::getInstance('jrportal_taxrate');
            $cfgcode = $mrConfig[ 'accommodation_tax_code' ];
            $accommodation_tax_rate = (float) $jrportal_taxrate->taxrates[ $cfgcode ][ 'rate' ];
        }

        $query = "SELECT `rates_uid`,`rate_title`,`rate_description`,`validfrom`,`validto`,
			`roomrateperday`,`mindays`,`maxdays`,`minpeople`,`maxpeople`,`roomclass_uid`,
			`ignore_pppn`,`allow_we`,`weekendonly`,`dayofweek`,`minrooms_alreadyselected`,`maxrooms_alreadyselected`
			FROM #__jomres_rates WHERE property_uid = ".$property_uid." 
			AND DATE_FORMAT(`validto`, '%Y/%m/%d') >= DATE_FORMAT('".$today."', '%Y/%m/%d')
			";

        $tariffs = doSelectSql($query);

        $interval = new DateInterval('P1D');
        foreach ($tariffs as $t) {
            $roomrate = $t->roomrateperday;
            if ($mrConfig[ 'prices_inclusive' ] == 1) {
                $divisor = ($accommodation_tax_rate / 100) + 1;
                $roomrate = $roomrate / $divisor;
            }
            
            $dates = webhook_get_periods($t->validfrom, $t->validto.' 23:59:59', $interval);
            
            $allPropertyTariffs[ $t->rates_uid ] = array(
                'rates_uid' => $t->rates_uid,
                'rate_title' => $t->rate_title,
                'rate_description' => $t->rate_description,
                'validfrom' => $t->validfrom,
                'validto' => $t->validto,
                
                'roomrateperday' => $roomrate,
                'mindays' => $t->mindays,
                'maxdays' => $t->maxdays,
                'minpeople' => $t->minpeople,
                'maxpeople' => $t->maxpeople,
                'roomclass_uid' => $t->roomclass_uid,
                'ignore_pppn' => $t->ignore_pppn,
                'allow_we' => $t->allow_we,
                'weekendonly' => $t->weekendonly,
                'dayofweek' => $t->dayofweek,
                'minrooms_alreadyselected' => $t->minrooms_alreadyselected,
                'maxrooms_alreadyselected' => $t->maxrooms_alreadyselected,
                'tariff_dates' => $dates,
                );
        }

        $response                               = new stdClass();
        $response->tariffs                      = $allPropertyTariffs;
        
        Flight::json( $response_name = "property_tariffs" ,$response);
    } catch (Exception $e) {
        Flight::halt( "404" ,"invalid_property_uid");
        }
	});

    function webhook_get_periods($start, $end, $interval = null)
    {
        $start = new DateTime($start);
        $end = new DateTime($end);
        if (is_null($interval)) {
            $interval = new DateInterval('P1D');
        }

        $period = new DatePeriod($start, $interval, $end);
        $dates = array();
        foreach ($period as $date) {
            $d = $date->format('Y/m/d');
            $dates[ ] = $d;
        }

        return $dates;
    }
    